    private void bagiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bagiActionPerformed
        double angka1 = Integer.parseInt(angka1field.getText());
        double angka2 = Integer.parseInt(angka2field.getText());
        double hasil = angka1 / angka2;
        hasilfield.setText(Double.toString(hasil));
    }//GEN-LAST:event_bagiActionPerformed

    private void kaliActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kaliActionPerformed
        int angka1 = Integer.parseInt(angka1field.getText());
        int angka2 = Integer.parseInt(angka2field.getText());
        int hasil = angka1 * angka2;
        hasilfield.setText(Integer.toString(hasil));
    }//GEN-LAST:event_kaliActionPerformed

