    private void tambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tambahActionPerformed
        int angka1 = Integer.parseInt(angka1field.getText());
        int angka2 = Integer.parseInt(angka2field.getText());
        int hasil = angka1 + angka2;
        hasilfield.setText(Integer.toString(hasil));
    }//GEN-LAST:event_tambahActionPerformed

    private void kurangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kurangActionPerformed
        int angka1 = Integer.parseInt(angka1field.getText());
        int angka2 = Integer.parseInt(angka2field.getText());
        int hasil = angka1 - angka2;
        hasilfield.setText(Integer.toString(hasil));
    }//GEN-LAST:event_kurangActionPerformed

    private void nolActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nolActionPerformed
        angka1field.setText("");
        angka2field.setText("");
        hasilfield.setText("");
    }//GEN-LAST:event_nolActionPerformed

